import {authAPI} from "../Api/api";

const storageName = 'userData'
const SET_USER_DATA = "auth/SET_USER_DATA"
const DELETE_USER_DATA = "DELETE_USER_DATA"

let initialState = {
    userId: null,
    token: null,
    isAuth: false,
    email: null,
    login: null,
}




const authReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_USER_DATA: {
            return {
                ...state,
                ...action.payload
            }
        }
        case DELETE_USER_DATA: {
            return {
                ...state,
                userId: null, email: null, login: null, isAuth: false, token: null
            }
        }
        default: return state
    }
}


export const setUserData = (auth, userId, email, login) => ({
    type: SET_USER_DATA, payload: {userId, isAuth: auth, email, login}
})

export const deleteUserData = () => ({
    type: DELETE_USER_DATA
})

export const loginUser = (email, password) => async (dispatch) => {
    try {
        let response = await authAPI.login(email, password)
        let {token, userId, email} = response.data
        console.log(token, userId, email)
        localStorage.setItem("token", token)
        dispatch(setUserData(true, userId, email ))
    }catch (e) {
        console.log(e.response)
    }
}





export default authReducer;
