import React from "react";
import {
    Link
} from "react-router-dom";
import {useDispatch, useSelector} from "react-redux";
import {Dropdown} from "react-bootstrap";
import DropdownButton from "react-bootstrap/DropdownButton";
import {useAuth} from "../Hooks/auth-hook";
import {deleteUserData} from "../Redux/auth-reducer";


export const Header = () => {

    const dispatch = useDispatch()

    const isAuth = useSelector(state => state.auth.isAuth)
    const email = useSelector(state => state.auth.email)
    const {logout} = useAuth()

    const logoutHandler = () => {
        logout()
        dispatch(deleteUserData())
    }

    return (
        <div className="header">
            <div className="container">
                <div className="header-inner">
                    <div className="header-left">
                        <h3 className="header-title">
                            <Link to="/new">LOGO</Link>
                        </h3>
                        <nav className="header-nav">
                            <ul className="header-nav__list">
                                <li className="header-nav__item">
                                    <Link to="/new">Hot</Link>
                                </li>
                                <li className="header-nav__item">
                                    <Link to="/best">Best</Link>
                                </li>
                                {isAuth && <li className="header-nav__item">
                                    <Link to="/my">My questions</Link>
                                </li>}
                            </ul>
                        </nav>
                    </div>
                    <div className="header-right">
                        <Link to="/ask">Ask the question</Link>
                        {isAuth ? <DropdownButton id="dropdown-basic-button" title={email}>
                            <Dropdown.Item as={Link} to="ask">Ask</Dropdown.Item>
                            <Dropdown.Item onClick={logoutHandler}>Logout</Dropdown.Item>
                        </DropdownButton> : <Link to={'/auth/login'}>Login</Link>}

                        <div className="header-user__icon">
                            <img src="" alt=""/>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
