import React, {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {useParams} from "react-router-dom"
import {getQuestionData} from "../../Redux/question-reducer";
import {Question} from "./Question";
import {Answer} from "./Answer";
import Loader from "../Loader";

export const QuestionPage = React.memo(function QuestionPage(props)  {

    const dispatch = useDispatch()
    const id = useParams().id

    useEffect(() => {
        dispatch(getQuestionData(id))
    }, [id])

    const question = useSelector(state => state.question.question)
    const answers = useSelector(state => state.question.answers)
    const isLoading = useSelector(state => state.question.isLoading)
    console.log("RENDER")



    const [value, setValue] = useState('')
    const onSubmit = (e) => {
        e.preventDefault()
        setValue('')
    }

    if (isLoading) {
        return <Loader/>
    }

    return (
        <div className="question">

            <Question text={question.text} title={question.title} likes={question.likes} author={question.author} date={question.date} answers={answers.length} />

            <form action="" className="question-form" onSubmit={onSubmit}>
                <div className="question-form__title">
                    <h3>Answer the question</h3>
                </div>
                <div className="question-form__content">
                    
                    <div className="question-form__img">
                        <img src="https://via.placeholder.com/50" alt=""/>
                    </div>
                    
                    <div className="question-form__input" >
                        <textarea type="text" value={value} onChange={(e) => setValue(e.target.value)} placeholder="Write your answer here"/>
                        <button className="btn" type={"submit"}>Send answer</button>
                    </div>
                </div>
               
            </form>

            <div className="answers">
                <div className="answers-title">
                    <h3>Answers({answers.length}):</h3>
                </div>
                <hr/>

                {answers.map(answer => {
                    return (
                       <Answer key={answer.id} date={answer.date} text={answer.text} likes={answer.likes} author={answer.author} />
                    )
                })}
            </div>

        </div>
    )
})
