import React, {useState} from "react";

export const AuthPage = () => {


    const [form, setForm] = useState({
        text: '',
        title: ''
    })

    const changeHandler = e => {
        setForm({...form, [e.target.name]: e.target.value})
    }

    return (
        <div className="auth">
            <div className="auth-title">
                <h1 >Ask the question</h1>
            </div>

            <form action="" className="ask-form">
                <label htmlFor="ask-title">Title question</label>
                <div className="ask-form__input">
                    <input name="title" onChange={changeHandler} type="text" id="aut"/>
                </div>

                <label htmlFor="ask-text">Text</label>
                <div className="ask-form__input">
                    <input value={form.text} name="text" id="ask-text" onChange={changeHandler}></input>
                </div>

                <button className="btn" type={"submit"}>Submit</button>
            </form>
        </div>
    )
}
