import React, {useState} from "react";
import Button from "react-bootstrap/Button";

export const AskPage = () => {

    const [form, setForm] = useState({
        text: '',
        title: ''
    })

    const changeHandler = e => {
        setForm({...form, [e.target.name]: e.target.value})
    }



    // const onSumbit = () => {
    //     if (form.text === '' || form.title === '') return false
    //     // Axios.post('/api')
    //
    // }

    return (
        <div className="ask">
            <div className="ask-title">
                <h1 >Ask the question</h1>

            </div>

            <form action="" className="ask-form">
                <label htmlFor="ask-title">Title question</label>
                <div className="ask-form__input">
                    <textarea name="title" onChange={changeHandler} type="text" id="ask-title"/>
                </div>

                <label htmlFor="ask-text">Text</label>
                <div className="ask-form__input">
                    <textarea value={form.text} name="text" id="ask-text" onChange={changeHandler}></textarea>
                </div>
                
                <Button className="btn" type={"submit"}>Submit</Button>
            </form>
        </div>
    )
}
