import * as axios from 'axios'


const instance = axios.create({
    baseURL: "http://localhost:3000/",
    // headers: {
    //     Authorization: `Bearer ${auth.token}`
    // }
})


export const authAPI = {
    login(email, password) {
        return instance.post("api/auth/login", {email })
    }
}
