const SET_USER_DATA = "auth/SET_USER_DATA"

let initialState = {
    userId: null,
    token: null,
    isAuth: false,
    email: null,
    login: null,
}


const authReducer = (state = initialState, action) => {
    switch (action.type) {
        default: return state
    }
}


export const setUserData = (auth, userId, token, email, login) => ({
    type: SET_USER_DATA, payload: {userId}
})


export default authReducer;
