import React, {useEffect, useState} from "react";
import {Link, useHistory, useParams} from "react-router-dom"
import * as axios from "axios";
import Message from "../Message";
import {useDispatch, useSelector} from "react-redux";
import {useAuth} from "../../Hooks/auth-hook";
import {setUserData} from "../../Redux/auth-reducer";
import Button from "react-bootstrap/Button";
import {Form} from "react-bootstrap";
import Alert from "react-bootstrap/Alert";


export const AuthPage = () => {


    const history = useHistory()
    const id = useParams().id
    const dispatch = useDispatch()
    const {login} = useAuth()
    const isAuth = useSelector(state => state.auth.isAuth)
    if (isAuth) {
        history.push('/')
    }
    const [form, setForm] = useState({
        email: '',
        password: '',
        repassword: ''
    })

    const [message, setMessage] = useState({error: null, message: ''})

    useEffect(() => {
        return function () {
            setTimeout(() => {
                setMessage({error: null, message: null})
            }, 5000)
        }
    }, [message])

    const changeHandler = e => {
        setForm({...form, [e.target.name]: e.target.value})
    }

    useEffect(() => {
        setForm({email: '', password: '', repassword: ''})
    }, [id])

    const registerHandler = async (e) => {
        try {
            e.preventDefault()
            let response = await axios.post('/api/auth/register', {email: form.email, password: form.password})
            history.push("/auth/login")
            setMessage({error: false, message: response.data.message})
            setForm({email: '', password: '', repassword: ''})
        } catch (e) {
            setMessage({error: true, message: e.response.data.message})
        }
    }

    const loginHandler = async (e) => {
        e.preventDefault()
        let response = await axios.post('/api/auth/login', {email: form.email, password: form.password})
        if (response.status === 200) {
            login(response.data.token, response.data.userId, response.data.email)
            dispatch(setUserData(true, response.data.token, response.data.userId, response.data.email))
        }
    }


    if (id === "login") {
        return (

            <div className="auth">
                <div className="auth-title">
                    <div><h3>Login</h3></div>
                    <div><Link to="/auth/register">Нет аккаунта?</Link></div>
                </div>
                {message.message && <Message error={message.error} message={message.message}/>}

                <Form onSubmit={loginHandler}>
                    <Form.Group controlId="formBasicEmail">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control name="email" onChange={changeHandler} value={form.email} type="email"
                                      placeholder="Enter email"/>
                        <Form.Text className="text-muted">
                            We'll never share your email with anyone else.
                        </Form.Text>
                    </Form.Group>
                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control name="password" onChange={changeHandler} value={form.password} type="password"
                                      placeholder="Password"/>
                    </Form.Group>

                    <Button className="btn" type="submit">Register</Button>
                </Form>

            </div>
        )
    } else if (id === "register") {
        return (

            <div className="auth">
                <div className="auth-title">
                    <div><h3>Register</h3></div>
                    <div><Link to="/auth/login">Есть аккаунт?</Link></div>
                </div>
                {message.message
                && <Alert variant="danger" onClose={() => setMessage({error: null, message:null})} dismissible>
                    <Alert.Heading>Oh snap! You got an error!</Alert.Heading>
                    <p>
                        Change this and that and try again. Duis mollis, est non commodo
                        luctus, nisi erat porttitor ligula, eget lacinia odio sem nec elit.
                        Cras mattis consectetur purus sit amet fermentum.
                    </p>
                </Alert>
                }
                <Form action="" onSubmit={registerHandler}>
                    <Form.Group controlId="formBasicEmail">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control name="email" onChange={changeHandler} value={form.email} type="email"
                                      placeholder="Enter email"/>
                        <Form.Text className="text-muted">
                            We'll never share your email with anyone else.
                        </Form.Text>
                    </Form.Group>
                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control name="password" onChange={changeHandler} value={form.password} type="password"
                                      placeholder="Password"/>
                    </Form.Group>

                    <Button className="btn" type="submit">Register</Button>
                </Form>


            </div>
        )
    }
}
