import React, {useState} from "react";
import Button from "react-bootstrap/Button";
import {useDispatch} from "react-redux";
import {postQuestion} from "../../Redux/questions-reducer";

export const AskPage = () => {

    const dispatch = useDispatch()

    const [form, setForm] = useState({
        text: '',
        title: ''
    })

    const changeHandler = e => {
        setForm({...form, [e.target.name]: e.target.value})
    }


    const onSumbit = (e) => {
        e.preventDefault()
        if (form.text === '' || form.title === '') return false
        dispatch(postQuestion(form.title, form.text))
    }

    return (
        <div className="ask">
            <div className="ask-title">
                <h1 >Ask the question</h1>

            </div>

            <Form action="" className="ask-form" onSubmit={onSumbit}>
                {/*<label htmlFor="ask-title">Title question</label>*/}
                {/*<div className="ask-form__input">*/}
                {/*    <textarea name="title"  type="text" id="ask-title"/>*/}
                {/*</div>*/}
                <Form.Group controlId="exampleForm.ControlTextarea1">
                    <Form.Label>Example textarea</Form.Label>
                    <Form.Control onChange={changeHandler} as="textarea" rows="4" />
                </Form.Group>

                <label htmlFor="ask-text">Text</label>
                <div className="ask-form__input">
                    <textarea value={form.text} name="text" id="ask-text" onChange={changeHandler}></textarea>
                </div>
                
                <Button className="btn" type={"submit"}>Submit</Button>
            </Form>
        </div>
    )
}
