const SET_USER_DATA = "auth/SET_USER_DATA"
const DELETE_USER_DATA = "DELETE_USER_DATA"

let initialState = {
    userId: null,
    token: null,
    isAuth: false,
    email: null,
    login: null,
}


const authReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_USER_DATA: {
            return {
                ...state,
                ...action.payload
            }
        }
        case DELETE_USER_DATA: {
            return {
                ...state,
                userId: null, email: null, login: null, isAuth: false, token: null
            }
        }
        default: return state
    }
}


export const setUserData = (auth, userId, token, email, login) => ({
    type: SET_USER_DATA, payload: {userId, isAuth: auth, token, email, login}
})

export const logout = () => ({
    type: DELETE_USER_DATA
})




export default authReducer;
