import React, {useState} from "react";
import getData from "../../utils/getData"

export const QuestionPage = () => {


    // const [answers, setAnswers] = useState([
    //     {
    //         author: "author",
    //         date: new Date(2020, 2, 16, 14, 20, 1),
    //         likes: 10,
    //         text: "Lorem fsgdfsg dfg dfg fdg fdg fdg fdg fdgfdgfdgfd"
    //     },
    //     {
    //         author: "author",
    //         date: new Date(2020, 2, 16, 14, 20, 1),
    //         likes: 10,
    //         text: "Lorem fsgdfsg dfg dfg fdg fdg fdg fdg fdgfdgfdgfd"
    //     },
    //     {
    //         author: "author",
    //         date: new Date(2020, 2, 16, 14, 20, 1),
    //         likes: 10,
    //         text: "Lorem fsgdfsg dfg dfg fdg fdg fdg fdg fdgfdgfdgfd",
    //     },
    // ])
    const [value, setValue] = useState('')

    const onSubmit = (e) => {
        e.preventDefault()
        setAnswers([...answers,
            {
                author: "author",
                date: new Date(),
                likes: 0,
                text: value,
            }
        ])
        setValue('')
    }

    return (
        <div className="question">
            <div className="question-content">
                <div className="question-title">
                    <h3>Title</h3>
                </div>
                <div className="question-subtitle">
                    Author at <span>11:53</span>
                </div>
                <p className="question-text">
                    lorem ipsum lorem ipsumlorem ipsumlorem ipsumlorem ipsumlorem ipsumlorem ipsumlorem ipsumlorem ipsum
                    lorem ipsumlorem ipsumlorem ipsumlorem ipsumlorem ipsum
                    lorem ipsumlorem ipsumlorem ipsumlorem ipsumlorem ipsumlorem ipsumlorem ipsumlorem ipsumlorem
                    ipsumlorem ipsum
                    lorem ipsumlorem ipsumlorem ipsumlorem ipsum
                </p>
                <div className="question-footer">
                    <span>Likes: 10</span>
                    <span>Answers: 123</span>
                </div>
            </div>

            <form action="" className="question-form" onSubmit={onSubmit}>
                <div className="question-form__title">
                    <h3>Answer the question</h3>
                </div>
                <div className="question-form__content">
                    
                    <div className="question-form__img">
                        <img src="https://via.placeholder.com/50" alt=""/>
                    </div>
                    
                    <div className="question-form__input" >
                        <textarea type="text" value={value} onChange={(e) => setValue(e.target.value)} placeholder="Write your answer here"/>
                        <button className="btn" type={"submit"}>Send answer</button>
                    </div>
                </div>
               
            </form>

            <div className="answers">
                <div className="answers-title">
                    <h3>Answers(3):</h3>
                </div>
                <hr/>

                {answers.map((answer, index) => {
                    return (
                        <div className="answers__item" key={index}>
                            <div className="answers__item-title">
                                {answer.author}<span> at {getData(answer.date)}</span>
                            </div>
                            <p className="answers__item-text">{answer.text}</p>
                            <div className="answers__item-footer">
                                <span>Likes: {answer.likes}</span>
                            </div>
                        </div>
                    )
                })}
            </div>

        </div>
    )
}
