import React, {useEffect, useState} from "react";
import {Link, useHistory, useParams} from "react-router-dom"
import {authAPI} from "../../Api/api";
import * as axios from "axios";
import Message from "../Message";


export const AuthPage = () => {


    const history = useHistory()
    const id = useParams().id

    const [form, setForm] = useState({
        email: '',
        password: '',
        repassword: ''
    })

    const [errorMessage, setErrorMessage] = useState("")

    useEffect(() => {
        return function () {
            setTimeout(() => {
                setErrorMessage("")
            }, 5000)
        }
    }, [errorMessage])

    const changeHandler = e => {
        setForm({...form, [e.target.name]: e.target.value})
    }

    useEffect(() => {
        setForm({email: '', password: '', repassword: ''})
    }, [id])

    const registerHandler = async (e) => {
        try {
            e.preventDefault()
            let response = await axios.post('/api/auth/register', {email: form.email, password: form.password})
            setForm({email: '', password: '', repassword: ''})
        }catch (e) {
            setErrorMessage(e.response.data.message)
        }
    }



    if (id === "login") {
        return (

            <div className="auth">
                <div className="auth-title">
                    <h3>Login</h3>
                </div>

                <form action="" className="auth-form">
                    <label htmlFor="auth-email">Email</label>
                    <div className="auth-form__input">
                        <input type="text" name="email" onChange={changeHandler} value={form.email} id="auth-email"/>
                    </div>
                    <label htmlFor="auth-password">Password</label>
                    <div className="auth-form__input">
                        <input type="password" name="password" onChange={changeHandler} value={form.password} id="auth-password"/>
                    </div>
                    <button className="btn">Login</button>
                </form>

                <Link to="/auth/register">Нет аккаунта?</Link>
            </div>
        )
    } else if (id === "register") {
        return (

            <div className="auth">
                <div className="auth-title">
                    <h3>Register</h3>
                    {errorMessage && <Message error={true} message={errorMessage} />}
                </div>

                <form action="" className="auth-form" onSubmit={registerHandler} >
                    <label htmlFor="auth-email">Email</label>
                    <div className="auth-form__input">
                        <input type="text" name="email" onChange={changeHandler} value={form.email} id="auth-email"/>
                    </div>
                    <label htmlFor="auth-password">Password</label>
                    <div className="auth-form__input">
                        <input type="password" name="password" onChange={changeHandler} value={form.password} id="auth-password"/>
                    </div>
                    {/*<label htmlFor="auth-password">Confirm Password</label>*/}
                    {/*<div className="auth-form__input">*/}
                    {/*    <input type="password" name="repassword" onChange={changeHandler} value={form.repassword} id="auth-password"/>*/}
                    {/*</div>*/}
                    <button className="btn" type="submit">Register</button>
                </form>

                <Link to="/auth/login">Есть аккаунт?</Link>
            </div>
        )
    }
}
