import React, {useEffect, useState} from "react";
import {Link, useHistory, useParams} from "react-router-dom"
import {authAPI} from "../../Api/api";
import * as axios from "axios";
import Message from "../Message";
import {useDispatch, useSelector} from "react-redux";
import {loginUser} from "../../Redux/auth-reducer";
import {useAuth} from "../../Hooks/auth-hook";


export const AuthPage = () => {


    const history = useHistory()
    const id = useParams().id
    const dispatch = useDispatch()

    const {login} = useAuth()


    const [form, setForm] = useState({
        email: '',
        password: '',
        repassword: ''
    })

    const [message, setMessage] = useState({error: null, message: ''})


    useEffect(() => {
        return function () {
            setTimeout(() => {
                setMessage({error: null, message: null})
            }, 5000)
        }
    }, [message])

    const changeHandler = e => {
        setForm({...form, [e.target.name]: e.target.value})
    }

    useEffect(() => {
        setForm({email: '', password: '', repassword: ''})
    }, [id])

    const registerHandler = async (e) => {
        try {
            e.preventDefault()
            let response = await axios.post('/api/auth/register', {email: form.email, password: form.password})
            history.push("/auth/login")
            setMessage({error: false, message: response.data.message})
            setForm({email: '', password: '', repassword: ''})
        }catch (e) {
            setMessage({error: true, message: e.response.data.message})
        }
    }

    const loginHandler = async (e) => {
        e.preventDefault()
        let response = await axios.post('/api/auth/login', {email: form.email, password: form.password})
        if (response.status === 200) {
            console.log(response)
        }
    }



    if (id === "login") {
        return (

            <div className="auth">
                <div className="auth-title">
                    <h3>Login</h3>
                    {message.message && <Message error={message.error} message={message.message} />}
                </div>

                <form action="" className="auth-form" onSubmit={loginHandler}>
                    <label htmlFor="auth-email">Email</label>
                    <div className="auth-form__input">
                        <input type="text" name="email" onChange={changeHandler} value={form.email} id="auth-email"/>
                    </div>
                    <label htmlFor="auth-password">Password</label>
                    <div className="auth-form__input">
                        <input type="password" name="password" onChange={changeHandler} value={form.password} id="auth-password"/>
                    </div>
                    <button className="btn">Login</button>
                </form>

                <Link to="/auth/register">Нет аккаунта?</Link>
            </div>
        )
    } else if (id === "register") {
        return (

            <div className="auth">
                <div className="auth-title">
                    <h3>Register</h3>
                    {message.message && <Message error={message.error} message={message.message} />}
                </div>

                <form action="" className="auth-form" onSubmit={registerHandler} >
                    <label htmlFor="auth-email">Email</label>
                    <div className="auth-form__input">
                        <input type="text" name="email" onChange={changeHandler} value={form.email} id="auth-email"/>
                    </div>
                    <label htmlFor="auth-password">Password</label>
                    <div className="auth-form__input">
                        <input type="password" name="password" onChange={changeHandler} value={form.password} id="auth-password"/>
                    </div>
                    {/*<label htmlFor="auth-password">Confirm Password</label>*/}
                    {/*<div className="auth-form__input">*/}
                    {/*    <input type="password" name="repassword" onChange={changeHandler} value={form.repassword} id="auth-password"/>*/}
                    {/*</div>*/}
                    <button className="btn" type="submit">Register</button>
                </form>

                <Link to="/auth/login">Есть аккаунт?</Link>
            </div>
        )
    }
}
