import React, {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {useParams} from "react-router-dom"
import {getQuestionData} from "../../Redux/question-reducer";
import {Question} from "./Question";
import {Answer} from "./Answers/Answer";
import Loader from "../Loader";
import {AnswersList} from "./Answers/AnswersList";
import {QuestionForm} from "./QuestionForm";

export const QuestionPage = React.memo(function QuestionPage(props) {

    const dispatch = useDispatch()
    const id = useParams().id
    const [ready, setReady] = useState(false)

    useEffect(() => {
        dispatch(getQuestionData(id))
    }, [id])

    const question = useSelector(state => state.question.question)
    const answers = useSelector(state => state.question.answers)


    const postComment = (value) => {
        console.log(value)
    }

    if (!ready) {
        return <Loader/>
    }

    return (
        <div className="question">
            <Question text={question.text} title={question.title} likes={question.likes} author={question.author}
                      date={question.date} answers={answers.length}/>
            <QuestionForm postComment={postComment}/>
            <AnswersList answers={answers}/>
        </div>
    )
})
