import React from "react";
import {
    Link
} from "react-router-dom";
import {useSelector} from "react-redux";
import {Dropdown} from "react-bootstrap";
import DropdownButton from "react-bootstrap/DropdownButton";


export const Header = () => {

    const isAuth = useSelector(state => state.auth.isAuth)
    const email = useSelector(state => state.auth.email)


    return (
        <div className="header">
            <div className="container">
                <div className="header-inner">
                    <div className="header-left">
                        <h3 className="header-title">
                            <Link to="/new">LOGO</Link>
                        </h3>
                        <nav className="header-nav">
                            <ul className="header-nav__list">
                                <li className="header-nav__item">
                                    <Link to="/new">Hot</Link>
                                </li>
                                <li className="header-nav__item">
                                    <Link to="/best">Best</Link>
                                </li>
                                {isAuth && <li className="header-nav__item">
                                    <Link to="/my">My questions</Link>
                                </li>}
                            </ul>
                        </nav>
                    </div>
                    <div className="header-right">
                        <div className="header-input">
                            {/*<input type="text" />*/}
                        </div>
                        {isAuth ? <Link to="/ask">Ask the question</Link> : <Link to={'/auth/login'}>Login</Link>}
                        <DropdownButton id="dropdown-basic-button" title={email}>
                            <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                            <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                            <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
                        </DropdownButton>
                        <div className="header-user__icon">
                            <img src="" alt=""/>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
