import React, {useEffect} from 'react';
import './App.scss';
import {Header} from "./Components/Header";
import {MainPage} from "./Components/MainPage/MainPage";
import {Redirect, Route, Switch} from "react-router-dom";
import {QuestionPage} from "./Components/QuestionPage/QuestionPage";
import {AskPage} from "./Components/AskPage/askPage";
import {AuthPage} from "./Components/AuthPage/AuthPage";
import {useAuth} from "./Hooks/auth-hook";
import Loader from "./Components/Loader";
import {useDispatch} from "react-redux";
import {setUserData} from "./Redux/auth-reducer";

function App() {

    const {token, login, logout, userId, ready, email} = useAuth()
    const isAuth = !!token

    const dispatch = useDispatch()
    useEffect(() => {
        dispatch(setUserData(isAuth, userId, token, email))
    }, [token])

    if (!ready) {
        return (
            <div className="container">
                <Loader/>
            </div>
        )
    }

    return (
            <div>
                <Header/>
                <div className="content">
                    <div className="container">
                       <div className="content-inner">
                           <Switch>
                               <Route path="/" exact>
                                   <MainPage/>
                               </Route>
                               <Route path="/question/:id" exact>
                                   <QuestionPage/>
                               </Route>
                               <Route path="/ask" exact>
                                   <AskPage/>
                               </Route>
                               <Route path="/auth/:id" exact>
                                   <AuthPage/>
                               </Route>
                               <Redirect to="/"/>
                           </Switch>
                       </div>
                    </div>
                </div>
            </div>

    );
}

export default App;
