import React, {useState} from "react";
import {Link} from "react-router-dom";
import {useSelector} from "react-redux";
import getData from "../../utils/getData"

export const MainPage = () => {

    const questions = useSelector(state => state.questions.questions)

    console.log(questions)

    return (
        <div className="main">
                <div className="main-inner">
                    {questions.map(question => {
                        return (
                            <div className="main-question">
                                <div className="main-question__title">
                                    <Link to="/question/1">{question.title}</Link>
                                </div>
                                <div className="main-question__subtitle">
                                    <p>{question.author} <span className="main-question__date">at {getData(question.date)}</span></p>
                                </div>
                                <div className="main-question__text">
                                    <p>{question.text}
                                    </p>
                                </div>
                                <div className="main-question__footer">
                                    <span>Likes: {question.likes} </span>
                                    <span>Comments: {question.comments}</span>
                                </div>
                            </div>
                        )
                    })}
                </div>
        </div>
    )
}
