import {questionsAPI} from "../Api/api";

const SET_QUESTION_DATA = "SET_QUESTION_DATA"
const SET_ANSWERS = "SET_ANSWERS"

let initialState = {
    question: {
        title: null,
        text: null,
        likes: null,
        author: null,
        date: null,
    },
    answers: [
    ],
    isLoading: true,
}


const questionReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_QUESTION_DATA: {
            return {
                ...state,
                question: {...action.payload},
                isLoading: false
            }
        }
        case SET_ANSWERS : {
            return {
                ...state,
                answers: action.answers
            }
        }
        default: return state
    }
}

const setQuestionData = (title, text, likes, author, date) => ({
    type: SET_QUESTION_DATA, payload: {title, text, likes, author, date}
})

const setAnswers = (answers) => ({
    type: SET_ANSWERS, answers
})


export const getQuestionData = (id) => async (dispatch) => {
    try {
        let response = await questionsAPI.getQuestion(id)
        const {title, text, likes, author, date} = response.data
        dispatch(setQuestionData(title, text, likes, author.email, date))
        dispatch(setAnswers(response.data.answers))
    }catch (e) {
        dispatch(setQuestionData(null, null, null, null, null))
        console.log(e)
    }
}




export default questionReducer;
