export const authAPI = {
    me(token) {
      return instance.get("api/auth/me", {} )
    },
    login(email, password) {
        return instance.post("api/auth/login", {email, password})
    },
    register(email, password) {
        return instance.post("api/auth/register", {email, password})
    }
}
